import React from 'react';
import CategoryItem from '../components/CategoryItem';
import DesignFlyer from '../imgs/graphic-design/graphic-design-poster.png';
import McfEvents from '../imgs/graphic-design/mcf-events.png';
import FamilyLeavePoster from '../imgs/graphic-design/family-leave.png';
import DeadlinePoster from '../imgs/graphic-design/deadline.png';
import ProgramGuide from '../imgs/graphic-design/mathematics-program-guide.jpg';
import Modal from '../components/Modal';

class GraphicDesign extends React.Component {
    constructor() {
        super();

        this.state = {index: null, open: false};

    }


    // send to openModal2() in Modal. Put control in modal to hopefully control state of open more to make it 

    openModal3 = (index) => {
        console.log("open3: " + index);
        this.setState({index: index});
        console.log("state: " + this.state.index);
        //document.getElementById('myModal').style.display = "block";
        this.setState({open: true});
        console.log("openModal - graphic design")

    }

    render() {
        console.log("rendered, state: " + this.state.index);
        console.log("rendered, openModal: " + this.state.open)
        const imgsUrls = [DesignFlyer, McfEvents];
        return (
            <div>
            <Modal imgsUrls={imgsUrls} modalIndex={this.state.index} isOpen={this.state.open}/>

                <section>
                    <h2 className="title">Graphic Design {this.state.index}</h2>
                </section>
                <section className="padded">
                    <div className="container">
                        <div className="row wrap">

                            <CategoryItem
                                image={DesignFlyer}
                                title="Graphic Design Seminar Flyer"
                                caption="Illustrator"
                                open={this.openModal3.bind(this, 0)}
                            />

                            <CategoryItem
                                image={McfEvents}
                                title="MCF Events Flyer"
                                caption="Illustrator"
                                open={this.openModal3.bind(this, 1)}
                            />

                            <CategoryItem
                                image={FamilyLeavePoster}
                                title="Paid Family Leave Poster"
                                caption="Illustrator"
                            />

                            <CategoryItem
                                image={DeadlinePoster}
                                custom={{ height: '90%' }}
                                title="Course Deadline Poster"
                                caption="Illustrator"
                            />

                            <CategoryItem
                                image={ProgramGuide}
                                custom={{ height: '90%' }}
                                title="Mathematics Program Guide"
                                caption="InDesign"
                            />

                        </div>
                    </div>
                </section>

            </div>
        )
    }
}

export default GraphicDesign;
