import React from 'react';
import Modal from './Modal';

const CategoryItem = (props) => {

    return(
        <figure className="col-12 col-sm-12 col-md-6 col-lg-4 port-container">
          <img 
          src={props.image} 
          alt={props.title} 
          onClick={document.getElementById('myModal').style.display = "block"}
          style={props.custom ? props.custom : {height:'100%'} } />
          <figcaption>
            <i>{props.title}</i><br />{props.caption}
          </figcaption>
        </figure>
    )
}

export default CategoryItem;
