import React from 'react';
import Carousel from './Carousel';

class Modal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {open: this.props.isOpen, index: null};
    }

    openModal = (index) => {
        document.getElementById('myModal').style.display = "block";
        this.setState({open: true, index: index});
        this.refs.carouselRef.updateIndex(index);
    }

    closeModal = () => {
        document.getElementById('myModal').style.display = "none";
        this.setState({open: false});
    }

    makeDots = () => {
        for (let i = 0; i <= this.props.imgsUrls.length; i++) {
            return (
            <span className="dot" onClick= {this.openModal.bind(this, i)}></span>
        )
        }

    }

    render() {       
        return (
            <div id="myModal" className="modal">
                <div className="modal-content">
                    <div className="close cursor" onClick={this.closeModal}>&times;</div>

                    {/*
                    <a className="prev" >&#10094;</a>
                    <a className="next">&#10095;</a>

                    <div className="caption-container">
                        <p id="caption"></p>
                    </div>
 */}

                    <Carousel ref="carouselRef" imgsUrls={this.props.imgsUrls} />
                </div>

                {/*
                    make another function to loop through and create appropriate amount of dots based on size of img array
                */}
                <div style={{ textAlign: 'center' }}>
                {this.makeDots}
                {/* 
                    <span className="dot" onClick= {this.openModal.bind(this, 0)}></span>
                    <span className="dot" onClick= {this.openModal.bind(this, 1)}></span>
                    <span className="dot" onClick= {this.openModal.bind(this, 2)}></span>
                    <span className="dot" onClick={this.openModal.bind(this, 3)}></span>
                    <span className="dot" onClick={this.openModal.bind(this, 4)}></span>
                    */}
                </div>
            </div>
        )
    }
}

export default Modal;
