import React from 'react';
import Carousel from './Carousel';

class Modal extends React.Component {
    constructor(props) {
        super(props);
  //      this.openModal2 = this.openModal2.bind(this);
        this.closeModal = this.closeModal.bind(this);

        // this.state = {index : this.props.modalIndex};
    }

    // openModal2 = (index) => {
    //     console.log("modal2");
    //     this.setState({index: index});
    //     document.getElementById('myModal').style.display = "block";
    // }

    closeModal = () => {
        document.getElementById('myModal').style.display = "none";
    }

    render() {
        console.log("Modal: " + this.props.index);
       // console.log("modal " + this.props.imgsUrls);
        return (
            <div id="myModal" className="modal">


                <div className="modal-content">
                    <div className="close cursor" onClick={this.closeModal}>&times;</div>

                    {/*
                    <a className="prev" >&#10094;</a>
                    <a className="next">&#10095;</a>

                    <div className="caption-container">
                        <p id="caption"></p>
                    </div>
 */}

                    <Carousel imgsUrls={this.props.imgsUrls} carouselIndex={this.state.index} />
                </div>
                <div style={{ textAlign: 'center' }}>
                    <span className="dot" ></span>
                    <span className="dot" ></span>
                    <span className="dot" ></span>
                    <span className="dot" ></span>
                    <span className="dot" ></span>
                </div>
            </div>
        )
    }
}

export default Modal;
